import React from 'react'

export default class TrueFalseButtons extends React.Component {

	button_functions = (event) => {
		this.props.get_answer(event, this.props.question_answer);
		this.props.answered();
		this.props.correct_answer(event, this.props.question_answer)
	}

	correct_answer = (user_answer, question_answer) => {
		console.log(question_answer === user_answer)
		// let response = (question_answer === user_answer) ? "Correct" : "Wrong"
		if (question_answer === user_answer) {
			return "Correct"
		} else {
			return "Wrong"
		}
	}


	render() {
		// console.log(this.props)
		return(
			<div className="question_card">
				<button value="true"
				 	onClick={this.button_functions}>
					True
				</button>
				<button value="false"
					onClick={this.button_functions}>
					False
				</button>
			</div>
		)
	}

}
