import React from 'react'
import TrueFalseButtons from './TrueFalseButtons.js'

export default class QuestionCard extends React.Component {

	state = {
		question_answered: false
	}

	toggle_answered = () => {
		this.setState({
			question_answered: true
		})
	}


	correct_answer = (event, question_answer) => {
		// console.log(this.props.question.answer)
		console.log(question_answer.toString() === event.target.value)

	}

	render(){

		// console.log(this.props.question.answer)
		// console.log(this.props.get_answer)


		return(
			<div className="question_card">
				<span>{this.props.question.text}</span>
				<br />
			{ !(this.state.question_answered) ? <TrueFalseButtons question_answer={this.props.question.answer} get_answer={this.props.get_answer} answered={this.toggle_answered} correct_answer={this.correct_answer} /> : "" }
			</div>
		)
	}

}
