import React from 'react'

export default class TrueFalseButtons extends React.Component {



		button_functions = (event) => {
			this.props.get_answer(event, this.props.question_answer);
			this.props.answered()
		}
	render() {
		console.log(this.props)
		return(
			<div className="question_card">
				<button value="true"
				 	// onClick={(event) => this.props.get_answer(event, this.props.question_answer), () => {this.props.answered()} }>
				 	onClick={this.button_functions}>

					True
				</button>
				<button value="false"
					onClick={(event) => this.props.get_answer(event, this.props.question_answer), () => {this.props.answered()} }>
					False
				</button>
			</div>
		)
	}

}
