import React from 'react'

export default class QuestionCard extends React.Component {

	state = {
		question_answered: false
	}

	toggle_answered = () => {
		this.setState({
			question_answered: true
		})
	}


	render(){

		// console.log(this.props)

		return(
			<div className="question_card">
				<span>{this.props.question.text}</span>
				<br />
	{ !(this.state.question_answered) ? <button value="true" onClick={(event) => this.props.get_answer(event, this.props.question.answer), this.toggle_answered }>True</button> : <button value="false" onClick={(event) => this.props.get_answer(event, this.props.question.answer) }>False</button> }
			</div>
		)
	}

}
